
public class manipula_cademas_II {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Extraer x numero de caracteres de una cadena ( substring )
		String frase="Hoy es un estupendo dia";
		String frase_resumen=frase.substring(4);
		String frase_resumen2=frase.substring(4,6);
		System.out.println(frase_resumen);
		System.out.println(frase_resumen2);

	}

}
